mapper.select:
select id, date, attendance_state, hall_state, users_id
from users_state


        and date = #{date}


        and attendance_state = #{attendanceState}


        and hall_state = #{hallState}


        and users_id = #{usersId}

select id, date, attendance_state, hall_state, users_id
from users_state

    Id = #{id}

SELECT users.user_name, users.date_of_birth, users.phone_number, village_hall.hall_name, village_hall.address FROM users JOIN village_hall ON users.village_hall_id = village_hall.Id JOIN users_state ON users.Id = users_state.users_id
and users_state.hall_state = 1
and users_state.date = #{date}
and users.village_hall_id = #{hallId}
